jQuery(document).ready(function (e) {
  "use strict";
  e(function () {
    e("#contact").validate({
      rules: {
        name: {
          required: !0,
          minlength: 2,
        },
        surname: {
          required: !0,
          minlength: 2,
        },
        email: {
          required: !0,
          email: !0,
        },
        subject: {
          required: !0,
        },
        message: {
          required: !0,
        },
      },
      messages: {
        name: {
          required: "Por favor escreva seu nome",
          minlength: "Por favor escreva seu nome corretamente",
        },
        surname: {
          required: "Por favor escreva seu sobrenome",
          minlength: "Por favor escreva seu sobrenome corretamente",
        },
        email: {
          required: "Por favor escreva seu e-mail corretamente",
        },
        subject: {
          required: "Por favor escreva seu número",
          minlength: "Número muito curto",
        },
        message: {
          required: "Por favor escreva sua mensagem",
          minlength: "mensagem  muito curta",
        },
      },
      submitHandler: function (r) {
        e(r).ajaxSubmit({
          type: "POST",
          data: e(r).serialize(),
          url: "php/process.php",
          success: function () {
            e("#contact :input").attr("disabled", "disabled"),
              e("#contact").fadeTo("slow", 0.15, function () {
                e(this).find(":input").attr("disabled", "disabled"),
                  e(this).find("label").css("cursor", "default"),
                  e("#success").fadeIn();
              });
          },
          error: function () {
            e("#contact").fadeTo("slow", 1, function () {
              e("#error").fadeIn();
            });
          },
        });
      },
    });
  });
});
